<?class RSS{	public function RSS()	{		require_once ('mysql_connect.php');	}		public function GetFeed()	{		return $this->getDetails() . $this->getItems();	}		private function dbConnect()	{		dbconnect();	}		private function getLink(){		$ini_array = parse_ini_file("settings.ini");		return $ini_array['vote_page'];	}		private function getDetails()	{		$detailsTable = "wp_files";		$this->dbConnect();		$query = "SELECT * FROM ". $detailsTable;		$result = mysql_query ($query);				while($row = mysql_fetch_array($result))		{			$details = '<?xml version="1.0" encoding="ISO-8859-1" ?>					<rss version="2.0" xmlns:atom="http://www.w3.org/2005/Atom">						<channel>							<atom:link href="http://localhost:8888/digitalstylistnetwork/wp-content/plugins/mcr/upload/rss.php" rel="self" type="application/rss+xml" />							<title>MCR</title>							<link>http://jhickeyiv.tumblr.com</link>							<description>MCR</description>							';		}		return $details;	}		private function getItems()	{		$itemsTable = "wp_files";		$this->dbConnect();		$query = "SELECT * FROM ". $itemsTable;		$result = mysql_query ($query);		$items = '';		while($row = mysql_fetch_array($result))		{			$items .= '<item>			<title>Vote for this at digitalstylistnetwork</title>			<description>http://localhost:8888/digitalstylistnetwork/?page_id=7&i='.$row["id"].'</description>			<link>'.$row["url"].'</link>			</item>';		}		$items .= '</channel>				 </rss>';		return $items;	}}?>